#include <stdio.h>
#include <stdint.h>
#include "HAL/hal_led.h"
#include "HAL/hal_switch.h"
#include "HAL/hal_keypad.h"

extern void initialise_monitor_handles(void);

int main(void) {
	initialise_monitor_handles();
	led_init();
	keypad_init();
	char volatile key;
	/* Loop forever */
	for(;;) {
		key = keypad_getPressedKey();
		if(key != (char)-1) {
			printf("The pressed key is %c\r\n",key);
		}
	}
}
